import { useEffect, useState } from "react";
import axios from "axios";
import AdminLayout from "./AdminLayout";
import "./AdminDashboard.css";

import { Bar, Pie, Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  LineElement,
  PointElement,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  LineElement,
  PointElement,
  Tooltip,
  Legend
);

export default function AdminDashboard() {
  const [data, setData] = useState(null);

  useEffect(() => {
    axios.get("/api/admin/dashboard/metrics")
      .then(res => setData(res.data))
      .catch(err => console.error(err));
  }, []);

  if (!data) return <p className="loading">Loading...</p>;

  const barData = {
    labels: ["Research", "SIH", "MSME", "Grants", "Publications", "Awards"],
    datasets: [
      {
        label: "Total Records",
        data: [
          data.research,
          data.sih,
          data.msme,
          data.grants,
          data.publications,
          data.awards
        ],
        backgroundColor: [
          "#2563eb",
          "#22c55e",
          "#f97316",
          "#eab308",
          "#6366f1",
          "#ef4444"
        ]
      }
    ]
  };

  const pieData = {
    labels: ["Students", "Faculty"],
    datasets: [
      {
        data: [data.students, data.faculty],
        backgroundColor: ["#3b82f6", "#10b981"]
      }
    ]
  };

  const lineData = {
    labels: ["Research", "SIH", "MSME", "Grants"],
    datasets: [
      {
        label: "Innovation Growth",
        data: [
          data.research,
          data.sih,
          data.msme,
          data.grants
        ],
        borderColor: "#2563eb",
        backgroundColor: "rgba(37,99,235,0.2)",
        tension: 0.4,
        fill: true
      }
    ]
  };

  return (
    <AdminLayout>
      <h2 className="dashboard-title">Admin System Overview</h2>

      {/* METRIC CARDS */}
      <div className="metrics-grid">
        {Object.entries(data).map(([key, value]) => (
          <div className={`metric-card ${key}`} key={key}>
            <h4>{key.toUpperCase()}</h4>
            <p>{value}</p>
          </div>
        ))}
      </div>

      {/* CHARTS */}
      <div className="charts-grid">
        <div className="chart-box">
          <h4>Overall Contributions</h4>
          <Bar data={barData} />
        </div>

        <div className="chart-box">
          <h4>User Distribution</h4>
          <Pie data={pieData} />
        </div>

        <div className="chart-box full">
          <h4>Innovation Trend</h4>
          <Line data={lineData} />
        </div>
      </div>
    </AdminLayout>
  );
}
