import React, { useState } from "react";
import { FaEye, FaEyeSlash } from "react-icons/fa";
import "./StudentRegistration.css";
import { useNavigate } from "react-router-dom";

export default function StudentRegistration() {
  const [formData, setFormData] = useState({
    name: "",
    department: "",
    year: "",
    roll: "",
    email: "",
    phone: "",
    password: "",
    confirmPassword: ""
  });

  const [errors, setErrors] = useState("");
  const [showPass, setShowPass] = useState(false);
  const [showConfirmPass, setShowConfirmPass] = useState(false);

  const departments = ["AI&DS", "AI&ML", "ASE", "CSE", "ECE", "Civil", "Mech"];
  const years = ["I", "II", "III", "IV"];

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };
const navigate = useNavigate();

  // Password Validation
  const validatePassword = (password) => {
    const regex =
      /^(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{6,15}$/;
    return regex.test(password);
  };

  const validateEmail = (email) => email.includes("@");

  const handleSubmit = (e) => {
  e.preventDefault();

  if (!validateEmail(formData.email)) {
    setErrors("Email must contain '@'");
    return;
  }

  if (!validatePassword(formData.password)) {
    setErrors(
      "Password must be 6–15 chars & include 1 uppercase, 1 number, 1 special character"
    );
    return;
  }

  if (formData.password !== formData.confirmPassword) {
    setErrors("Passwords do not match");
    return;
  }

  setErrors("");
  alert("Registration Successful!");
  navigate("/login");   // ✅ Redirect to login page
};


  return (
    <div className="reg-container">

      {/* LEFT ILLUSTRATION PANEL */}
      <div className="reg-left">
        <h2>WELCOME TO</h2>
        <h1>INNOVATEHUB</h1>

        <img 
          src="/student-bg.png" 
          alt="illustration"
          className="reg-illustration"
        />
      </div>

      {/* RIGHT FORM PANEL */}
      <div className="reg-right">

        <div className="reg-box">
          <h2>Student Registration</h2>

          {errors && <p className="error">{errors}</p>}

          <form onSubmit={handleSubmit}>

            <input
              type="text"
              name="name"
              placeholder="Full Name"
              value={formData.name}
              onChange={handleChange}
              required
            />

            <select name="department" value={formData.department} onChange={handleChange} required>
              <option value="">Select Department</option>
              {departments.map((d) => (
                <option key={d} value={d}>{d}</option>
              ))}
            </select>

            <select name="year" value={formData.year} onChange={handleChange} required>
              <option value="">Year of Study</option>
              {years.map((y) => (
                <option key={y} value={y}>{y}</option>
              ))}
            </select>

            <input
              type="text"
              name="roll"
              placeholder="Roll Number"
              value={formData.roll}
              onChange={handleChange}
              required
            />

            <input
              type="email"
              name="email"
              placeholder="Email Address"
              value={formData.email}
              onChange={handleChange}
              required
            />

            <input
              type="text"
              name="phone"
              placeholder="Phone Number"
              value={formData.phone}
              onChange={handleChange}
              required
            />

            {/* PASSWORD FIELD WITH TOGGLE */}
            <div className="pass-wrapper">
              <input
                type={showPass ? "text" : "password"}
                name="password"
                placeholder="Password"
                value={formData.password}
                onChange={handleChange}
                required
              />
              <span onClick={() => setShowPass(!showPass)} className="eye-icon">
                {showPass ? <FaEyeSlash /> : <FaEye />}
              </span>
            </div>

            {/* CONFIRM PASSWORD FIELD */}
            <div className="pass-wrapper">
              <input
                type={showConfirmPass ? "text" : "password"}
                name="confirmPassword"
                placeholder="Confirm Password"
                value={formData.confirmPassword}
                onChange={handleChange}
                required
              />
              <span onClick={() => setShowConfirmPass(!showConfirmPass)} className="eye-icon">
                {showConfirmPass ? <FaEyeSlash /> : <FaEye />}
              </span>
            </div>

            <button type="submit">Create Account</button>
          </form>

          <p className="login-text">
            Already Registered? <a href="/login">Login</a>
          </p>

        </div>
      </div>
    </div>
  );
}
