import React, { useRef, useState, useEffect } from "react";
import Webcam from "react-webcam";
import * as faceapi from "face-api.js";
import axios from "axios";

export default function FaceVerifyModal({ onSuccess, onClose }) {
  const webcamRef = useRef(null);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  /* ===============================
     LOAD MODELS (SSD ONLY)
  =============================== */
  useEffect(() => {
    const loadModels = async () => {
      await Promise.all([
        faceapi.nets.ssdMobilenetv1.loadFromUri("/models"),
        faceapi.nets.faceLandmark68Net.loadFromUri("/models"),
        faceapi.nets.faceRecognitionNet.loadFromUri("/models"),
      ]);
    };
    loadModels();
  }, []);

  /* ===============================
     VERIFY FACE
  =============================== */
  const verifyFace = async () => {
    setError("");
    setLoading(true);

    const imageSrc = webcamRef.current.getScreenshot();
    if (!imageSrc) {
      setError("Camera not ready");
      setLoading(false);
      return;
    }

    const img = new Image();
    img.src = imageSrc;

    img.onload = async () => {
      try {
        const detections = await faceapi
          .detectAllFaces(
            img,
            new faceapi.SsdMobilenetv1Options({ minConfidence: 0.6 })
          )
          .withFaceLandmarks()
          .withFaceDescriptors();

        if (detections.length === 0) {
          setError("No face detected. Please try again.");
          setLoading(false);
          return;
        }

        if (detections.length > 1) {
          setError("Multiple faces detected. Only one person allowed.");
          setLoading(false);
          return;
        }

        // ✅ EXACTLY ONE FACE
        const currentDescriptor = Array.from(detections[0].descriptor);

        await axios.post("/api/face/verify", {
          currentDescriptor,
        });

        setLoading(false);
        onSuccess(); // ✅ VERIFIED

      } catch (err) {
        setError("Face does not match registration");
        setLoading(false);
      }
    };
  };

  return (
    <div className="modal-overlay">
      <div className="modal-box">
        <h3>Face Verification</h3>

        <Webcam
          ref={webcamRef}
          screenshotFormat="image/jpeg"
          className="face-webcam"
        />

        {error && <p className="error">{error}</p>}

        <button onClick={verifyFace} disabled={loading}>
          {loading ? "Verifying..." : "Capture & Verify"}
        </button>

        <button className="cancel" onClick={onClose}>
          Cancel
        </button>
      </div>
    </div>
  );
}
