import React, { useState, useRef, useEffect } from "react";
import "./Chatbot.css";
import { FaComments, FaTimes } from "react-icons/fa";

export default function Chatbot() {
  const [open, setOpen] = useState(false);
  const [messages, setMessages] = useState([
    { from: "bot", text: "Hi üëã I am InnovateHub Assistant. How can I help you?" }
  ]);
  const [input, setInput] = useState("");
  const messagesEndRef = useRef(null);

  /* auto scroll to bottom */
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { from: "user", text: input };
    setMessages((prev) => [...prev, userMessage]);
    setInput("");

    try {
      const response = await fetch(
        "http://localhost:5000/api/chatbot/chat",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json"
          },
          body: JSON.stringify({
            message: input,
            userId: localStorage.getItem("userId"),
            role: localStorage.getItem("role") // student / faculty
          })
        }
      );

      const data = await response.json();

      const botMessage = {
        from: "bot",
        text: data.reply || "Sorry üòï I couldn't understand that."
      };

      setMessages((prev) => [...prev, botMessage]);
    } catch (error) {
      setMessages((prev) => [
        ...prev,
        {
          from: "bot",
          text: "‚ö†Ô∏è Server error. Please try again later."
        }
      ]);
    }
  };

  return (
    <>
      {/* üîò Floating Chat Icon */}
      <div className="chatbot-icon" onClick={() => setOpen(!open)}>
        {open ? <FaTimes /> : <FaComments />}
      </div>

      {/* üí¨ Chat Window */}
      {open && (
        <div className="chatbot-box">
          {/* Header */}
          <div className="chatbot-header">
            <span>InnovateHub Assistant ü§ñ</span>
            <FaTimes
              className="close-icon"
              onClick={() => setOpen(false)}
            />
          </div>

          {/* Messages */}
          <div className="chatbot-messages">
            {messages.map((msg, index) => (
              <div
                key={index}
                className={`chat-msg ${
                  msg.from === "bot" ? "bot" : "user"
                }`}
              >
                {msg.text}
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>

          {/* Input */}
          <div className="chatbot-input">
            <input
              type="text"
              placeholder="Ask about Research, SIH, MSME, Grants..."
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => e.key === "Enter" && sendMessage()}
            />
            <button onClick={sendMessage}>Send</button>
          </div>
        </div>
      )}
    </>
  );
}
