import React, { useState } from "react";
import axios from "axios";
import "./ResearchPapers.css";

export default function ResearchPapers() {
  const [formData, setFormData] = useState({});
  const [pdf, setPdf] = useState(null);

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    Object.keys(formData).forEach((key) =>
      data.append(key, formData[key])
    );
    if (pdf) data.append("paperPdf", pdf);

    await axios.post(
      "http://localhost:5000/api/research-papers/add",
      data
    );

    alert("Research paper added successfully");
  };

  return (
    <div className="page-container">
      <div className="content-card">
        <h2 className="page-title">Add Research Paper</h2>

        <form className="form-grid" onSubmit={handleSubmit}>
          <input name="paperTitle" placeholder="Paper Title" onChange={handleChange} />
          <input name="authors" placeholder="Authors" onChange={handleChange} />
          <input name="correspondingAuthor" placeholder="Corresponding Author" onChange={handleChange} />
          <input name="journalName" placeholder="Journal / Conference Name" onChange={handleChange} />

          <select name="publisher" onChange={handleChange}>
            <option value="">Select Publisher</option>
            <option>IEEE</option>
            <option>Springer</option>
            <option>Elsevier</option>
            <option>ACM</option>
            <option>Others</option>
          </select>

          <select name="publicationType" onChange={handleChange}>
            <option value="">Publication Type</option>
            <option>Journal</option>
            <option>Conference</option>
          </select>

          <input name="volume" placeholder="Volume" onChange={handleChange} />
          <input name="issue" placeholder="Issue" onChange={handleChange} />
          <input name="pageNumbers" placeholder="Page Numbers" onChange={handleChange} />
          <input type="date" name="publicationDate" onChange={handleChange} />
          <input type="number" name="citationCount" placeholder="Citation Count" onChange={handleChange} />

          <input type="file" onChange={(e) => setPdf(e.target.files[0])} />

          <button type="submit" className="primary-btn">
            Add Research Paper
          </button>
        </form>
      </div>
    </div>
  );
}
