import React, { useState, useRef, useEffect } from "react";
import { FaEye, FaEyeSlash } from "react-icons/fa";
import { useNavigate } from "react-router-dom";
import "./StudentRegistration.css";
import Webcam from "react-webcam";
import * as faceapi from "face-api.js";

export default function StudentRegistration() {

  /* =====================
     REFS & NAVIGATION
  ====================== */
  const webcamRef = useRef(null);
  const navigate = useNavigate();

  /* =====================
     FORM STATE
  ====================== */
  const [formData, setFormData] = useState({
    name: "",
    department: "",
    year: "",
    roll: "",
    email: "",
    phone: "",
    password: "",
    confirmPassword: ""
  });

  const [errors, setErrors] = useState("");
  const [showPass, setShowPass] = useState(false);
  const [showConfirmPass, setShowConfirmPass] = useState(false);

  /* =====================
     FACE AUTH STATE
  ====================== */
  const [cameraOn, setCameraOn] = useState(false);
  const [faceDescriptor, setFaceDescriptor] = useState(null);
  const [faceCaptured, setFaceCaptured] = useState(false);
  const [modelsLoaded, setModelsLoaded] = useState(false); // ðŸ” DEFENSIVE

  const departments = ["AI&DS", "AI&ML", "ASE", "CSE", "ECE", "Civil", "Mech"];
  const years = ["I", "II", "III", "IV"];

  /* =====================
     LOAD FACE-API MODELS
  ====================== */
  useEffect(() => {
    const loadModels = async () => {
      try {
        await Promise.all([
          faceapi.nets.ssdMobilenetv1.loadFromUri("/models"),
          faceapi.nets.faceLandmark68Net.loadFromUri("/models"),
          faceapi.nets.faceRecognitionNet.loadFromUri("/models"),
        ]);
        setModelsLoaded(true);
      } catch (err) {
        console.error(err);
        setErrors("Failed to load face models");
      }
    };
    loadModels();
  }, []);

  /* =====================
     HANDLERS
  ====================== */
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const validatePassword = (password) => {
    const regex =
      /^(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{6,15}$/;
    return regex.test(password);
  };

  const validateEmail = (email) => email.includes("@");

  /* =====================
     FACE CAPTURE
  ====================== */
  const captureFace = async () => {
    if (!modelsLoaded) {
      setErrors("Face models still loading...");
      return;
    }

    const video = webcamRef.current?.video;
    if (!video) {
      setErrors("Camera not ready");
      return;
    }

    const detection = await faceapi
      .detectSingleFace(video)
      .withFaceLandmarks()
      .withFaceDescriptor();

    if (!detection) {
      setErrors("Face not detected. Please try again.");
      return;
    }

    setFaceDescriptor(Array.from(detection.descriptor));
    setFaceCaptured(true);
    setCameraOn(false); // âœ… CAMERA OFF
  };

  /* =====================
     SUBMIT
  ====================== */
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateEmail(formData.email)) {
      setErrors("Email must contain '@'");
      return;
    }

    if (!validatePassword(formData.password)) {
      setErrors(
        "Password must be 6â€“15 characters & include 1 uppercase, 1 number, 1 special character"
      );
      return;
    }

    if (formData.password !== formData.confirmPassword) {
      setErrors("Passwords do not match");
      return;
    }

    if (!faceDescriptor) {
      setErrors("Please capture your face");
      return;
    }

    try {
      const res = await fetch("http://localhost:5000/api/student/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          ...formData,
          faceDescriptor
        })
      });

      const data = await res.json();

      if (!res.ok) {
        setErrors(data.message);
        return;
      }

      navigate("/login");
    } catch (error) {
      setErrors("Server error. Please try again.");
    }
  };

  /* =====================
     JSX
  ====================== */
  return (
    <div className="reg-container">
      <div className="reg-card">

        <div className="reg-left">
          <img
            src="/registration-illustration.jpg"
            alt="illustration"
            className="reg-illustration"
          />
        </div>

        <div className="reg-right">
          <div className="reg-box">
            <h2>Student Registration</h2>

            {errors && <p className="error">{errors}</p>}

            <form onSubmit={handleSubmit}>

              <input name="name" placeholder="Full Name" onChange={handleChange} required />

              <select name="department" onChange={handleChange} required>
                <option value="">Select Department</option>
                {departments.map(d => <option key={d}>{d}</option>)}
              </select>

              <select name="year" onChange={handleChange} required>
                <option value="">Year</option>
                {years.map(y => <option key={y}>{y}</option>)}
              </select>

              <input name="roll" placeholder="Roll Number" onChange={handleChange} required />
              <input name="email" type="email" placeholder="Email" onChange={handleChange} required />
              <input name="phone" placeholder="Phone" onChange={handleChange} required />

              <div className="pass-wrapper">
                <input
                  type={showPass ? "text" : "password"}
                  name="password"
                  placeholder="Password"
                  onChange={handleChange}
                  required
                />
                <span className="eye-icon" onClick={() => setShowPass(!showPass)}>
                  {showPass ? <FaEyeSlash /> : <FaEye />}
                </span>
              </div>

              <div className="pass-wrapper">
                <input
                  type={showConfirmPass ? "text" : "password"}
                  name="confirmPassword"
                  placeholder="Confirm Password"
                  onChange={handleChange}
                  required
                />
                <span className="eye-icon" onClick={() => setShowConfirmPass(!showConfirmPass)}>
                  {showConfirmPass ? <FaEyeSlash /> : <FaEye />}
                </span>
              </div>

              {/* FACE AUTH */}
              <div className="face-auth-box">
                {!faceCaptured && (
                  <button
                    type="button"
                    className="face-btn"
                    disabled={!modelsLoaded}
                    onClick={() => setCameraOn(true)}
                  >
                    {modelsLoaded ? "Capture Face" : "Loading Models..."}
                  </button>
                )}

                {cameraOn && (
                  <>
                    <Webcam ref={webcamRef} className="face-webcam" />
                    <button type="button" className="face-btn" onClick={captureFace}>
                      Capture Image
                    </button>
                  </>
                )}

                {faceCaptured && (
                  <>
                    <p className="face-success">âœ” Face Captured</p>
                    <button
                      type="button"
                      className="face-btn"
                      onClick={() => {
                        setFaceCaptured(false);
                        setCameraOn(true);
                      }}
                    >
                      Re-Capture
                    </button>
                  </>
                )}
              </div>

              <button type="submit">Create Account</button>
            </form>

            <p className="login-text">
              Already Registered? <a href="/login">Login</a>
            </p>

          </div>
        </div>

      </div>
    </div>
  );
}
