import React, { useState } from "react";
import Sidebar from "./Sidebar";
import Header from "./Header";
import ResearchPapers from "./ResearchPapers";
import "./StudentDashboard.css";

export default function StudentDashboard() {
  const [activeSection, setActiveSection] = useState("dashboard");

  return (
    <div className="dashboard-wrapper">
      {/* Sidebar */}
      <Sidebar
        activeSection={activeSection}
        setActiveSection={setActiveSection}
      />

      {/* Main Content */}
      <div className="dashboard-main">
        {/* Top Header (shows Welcome, venkat once) */}
        <Header />

        <div className="dashboard-content">
          {/* DASHBOARD METRICS */}
          {activeSection === "dashboard" && (
            <>
              <h3>Your Recent Metrics</h3>

              <div className="metrics-grid">
                <div className="metric-box">
                  <h4>Publications</h4>
                  <p>7 this year</p>
                </div>

                <div className="metric-box">
                  <h4>Patents Filed</h4>
                  <p>5 total</p>
                </div>

                <div className="metric-box">
                  <h4>Grants Received</h4>
                  <p>$200,000</p>
                </div>

                <div className="metric-box">
                  <h4>Research Impact Score</h4>
                  <p>88</p>
                </div>
              </div>
            </>
          )}

          {/* RESEARCH PAPERS FORM */}
          {activeSection === "researchPapers" && (
            <ResearchPapers />
          )}

          {/* PLACEHOLDER FOR OTHER SECTIONS */}
          {activeSection !== "dashboard" &&
            activeSection !== "researchPapers" && (
              <div className="metric-box" style={{ marginTop: "20px" }}>
                <h4>Module Coming Soon</h4>
                <p>This section is under development.</p>
              </div>
            )}
        </div>
      </div>
    </div>
  );
}
