import React, { useEffect, useState } from "react";
import axios from "axios";
import "./ViewCertificates.css";

export default function ViewCertificates({ role, userId }) {
  const [certs, setCerts] = useState([]);

  useEffect(() => {
    axios
      .get(`/api/certificates/${role}/${userId}`)
      .then(res => setCerts(res.data));
  }, [role, userId]);

  if (certs.length === 0) {
    return <p className="no-cert">No certificates available</p>;
  }

  return (
    <div className="cert-list">
      {certs.map(c => (
        <div key={c._id} className="cert-card">
          <h4>{c.title}</h4>
          <p>Issued on: {new Date(c.issuedAt).toDateString()}</p>
          <a href={`/api/download/${c.fileName}`}>Download PDF</a>
        </div>
      ))}
    </div>
  );
}
