import React, { useState, useRef, useEffect } from "react";
import { FaEye, FaEyeSlash } from "react-icons/fa";
import { useNavigate } from "react-router-dom";
import "./StudentRegistration.css";
import Webcam from "react-webcam";
import * as faceapi from "face-api.js";


export default function StudentRegistration() {
  const [formData, setFormData] = useState({
    name: "",
    department: "",
    year: "",
    roll: "",
    email: "",
    phone: "",
    password: "",
    confirmPassword: ""
  });

  const [errors, setErrors] = useState("");
  const [showPass, setShowPass] = useState(false);
  const [showConfirmPass, setShowConfirmPass] = useState(false);

  const [cameraOn, setCameraOn] = useState(false);
  const [faceDescriptor, setFaceDescriptor] = useState(null);
  const [faceCaptured, setFaceCaptured] = useState(false);

  const navigate = useNavigate();

  const departments = ["AI&DS", "AI&ML", "ASE", "CSE", "ECE", "Civil", "Mech"];
  const years = ["I", "II", "III", "IV"];

  // LOAD FACE-API MODELS
 useEffect(() => {
  const loadModels = async () => {
    await Promise.all([
      faceapi.nets.ssdMobilenetv1.loadFromUri("/models"),
      faceapi.nets.faceLandmark68Net.loadFromUri("/models"),
      faceapi.nets.faceRecognitionNet.loadFromUri("/models"),
    ]);
  };
  loadModels();
}, []);

  // Handle Input Change
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Password Validation
  const validatePassword = (password) => {
    const regex =
      /^(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{6,15}$/;
    return regex.test(password);
  };
   const validateEmail = (email) => email.includes("@");
const webcamRef = useRef(null);

  // FACE CAPTURE
 const captureFace = async () => {
  const video = webcamRef.current.video;

  const detection = await faceapi
    .detectSingleFace(video) // ⚠️ NO Tiny options
    .withFaceLandmarks()
    .withFaceDescriptor();

  if (!detection) {
    setErrors("Face not detected. Please try again.");
    return;
  }

  setFaceDescriptor(Array.from(detection.descriptor));
  setFaceCaptured(true);
  setCameraOn(false);
};


 

  // Form Submit + Send Data to Backend
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateEmail(formData.email)) {
      setErrors("Email must contain '@'");
      return;
    }

    if (!validatePassword(formData.password)) {
      setErrors(
        "Password must be 6–15 characters & include 1 uppercase, 1 number, 1 special character"
      );
      return;
    }

    if (formData.password !== formData.confirmPassword) {
      setErrors("Passwords do not match");
      return;
    }

     if (!faceDescriptor) {
      setErrors("Please capture your face");
      return;
    }

     try {
      const res = await fetch("http://localhost:5000/api/student/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          ...formData,
          faceDescriptor
        })
      });

      const data = await response.json();

      if (!response.ok) {
        setErrors(data.message);
        return;
      }
      
      navigate("/login");
    } catch (error) {
      setErrors("Server error. Please try again.");
    }
  };

  return (
    <div className="reg-container">
      <div className="reg-card">

        {/* LEFT PANEL */}
        <div className="reg-left">
          <img
            src="/registration-illustration.jpg"
            alt="illustration"
            className="reg-illustration"
          />
        </div>

        {/* RIGHT PANEL */}
        <div className="reg-right">
          <div className="reg-box">
            <h2>Student Registration</h2>

            {errors && <p className="error">{errors}</p>}

            <form onSubmit={handleSubmit}>
              
              <input
                type="text"
                name="name"
                placeholder="Full Name"
                value={formData.name}
                onChange={handleChange}
                required
              />

              <select
                name="department"
                value={formData.department}
                onChange={handleChange}
                required
              >
                <option value="">Select Department</option>
                {departments.map((d) => (
                  <option key={d} value={d}>
                    {d}
                  </option>
                ))}
              </select>

              <select
                name="year"
                value={formData.year}
                onChange={handleChange}
                required
              >
                <option value="">Year of Study</option>
                {years.map((y) => (
                  <option key={y} value={y}>
                    {y}
                  </option>
                ))}
              </select>

              <input
                type="text"
                name="roll"
                placeholder="Roll Number"
                value={formData.roll}
                onChange={handleChange}
                required
              />

              <input
                type="email"
                name="email"
                placeholder="Email Address"
                value={formData.email}
                onChange={handleChange}
                required
              />

              <input
                type="text"
                name="phone"
                placeholder="Phone Number"
                value={formData.phone}
                onChange={handleChange}
                required
              />

              {/* PASSWORD FIELD */}
              <div className="pass-wrapper">
                <input
                  type={showPass ? "text" : "password"}
                  name="password"
                  placeholder="Password"
                  value={formData.password}
                  onChange={handleChange}
                  required
                />
                <span
                  className="eye-icon"
                  onClick={() => setShowPass(!showPass)}
                >
                  {showPass ? <FaEyeSlash /> : <FaEye />}
                </span>
              </div>

              {/* CONFIRM PASSWORD */}
              <div className="pass-wrapper">
                <input
                  type={showConfirmPass ? "text" : "password"}
                  name="confirmPassword"
                  placeholder="Confirm Password"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  required
                />
                <span
                  className="eye-icon"
                  onClick={() => setShowConfirmPass(!showConfirmPass)}
                >
                  {showConfirmPass ? <FaEyeSlash /> : <FaEye />}
                </span>
              </div>
                {/* FACE AUTH */}
              <div className="face-auth-box">
                {!faceCaptured && (
                  <button type="button" className="face-btn" onClick={() => setCameraOn(true)}>
                    Capture Face
                  </button>
                )}

                {cameraOn && (
                  <>
                    <Webcam ref={webcamRef} className="face-webcam" />
                    <button type="button" className="face-btn" onClick={captureFace}>
                      Capture Image
                    </button>
                  </>
                )}

                {faceCaptured && (
                  <>
                    <p className="face-success">✔ Face Captured</p>
                    <button
                      type="button"
                      className="face-btn"
                      onClick={() => {
                        setFaceCaptured(false);
                        setCameraOn(true);
                      }}
                    >
                      Re-Capture
                    </button>
                  </>
                )}
              </div>
              <button type="submit">Create Account</button>
            </form>

            <p className="login-text">
              Already Registered? <a href="/login">Login</a>
            </p>
          </div>
        </div>

      </div>
    </div>
  );
}
