import React from "react";
import { BrowserRouter as Router, Routes, Route, useLocation } from "react-router-dom";

import Navbar from "./components/Navbar";
import RegistrationBoxes from "./components/RegistrationBoxes";
import Footer from "./components/Footer";
import StudentRegistration from "./components/StudentRegistration";
import Login from "./components/Login";
import StudentDashboard from "./components/StudentDashboard";
import "./App.css";

function AppLayout() {
  const location = useLocation();

  // Routes where we don't want Navbar & Footer
  const hideNavFooter = ["/student-dashboard"];

  const isHidden = hideNavFooter.includes(location.pathname);

  return (
    <>
      {/* Show Navbar only if NOT on dashboard */}
      {!isHidden && <Navbar />}

      <main>
        <Routes>
          {/* Home page with three boxes */}
          <Route path="/" element={<RegistrationBoxes />} />

          {/* Student Registration Form */}
          <Route path="/student-register" element={<StudentRegistration />} />
          <Route path="/login" element={<Login />} />

          {/* Dashboard (no navbar/footer) */}
          <Route path="/student-dashboard" element={<StudentDashboard />} />
        </Routes>
      </main>

      {/* Show Footer only if NOT on dashboard */}
      {!isHidden && <Footer />}
    </>
  );
}

export default function App() {
  return (
    <Router>
      <AppLayout />
    </Router>
  );
}
