import React from "react";
import {
  FaHome,
  FaFileAlt,
  FaRocket,
  FaBook,
  FaMoneyBill,
  FaAward
} from "react-icons/fa";

export default function FacultySidebar({ activeSection, setActiveSection }) {
  const menu = [
    { key: "dashboard", label: "Dashboard", icon: <FaHome /> },
    { key: "researchPapers", label: "Research Papers", icon: <FaFileAlt /> },
    { key: "startup", label: "Startup Activities", icon: <FaRocket /> },
    { key: "publications", label: "Publications", icon: <FaBook /> },
    { key: "grants", label: "Grants & Funding", icon: <FaMoneyBill /> },
    { key: "sih", label: "SIH" },
    { key: "msme", label: "MSME" },
    { key: "recognition", label: "Awards", icon: <FaAward /> }
  ];

  return (
    <div className="sidebar">
      <h2 className="sidebar-title">Faculty Panel</h2>

      {menu.map((item) => (
        <div
          key={item.key}
          className={`sidebar-item ${
            activeSection === item.key ? "active" : ""
          }`}
          onClick={() => setActiveSection(item.key)}
        >
          {item.icon} {item.label}
        </div>
      ))}
    </div>
  );
}
