import React, { useState } from "react";
import axios from "axios";
import "./ResearchPapers.css";

export default function ResearchPapers() {
  const initialState = {
    paperTitle: "",
    authors: "",
    correspondingAuthor: "",
    journalName: "",
    publisher: "IEEE",
    publicationType: "Journal",
    volume: "",
    issue: "",
    pageNumbers: "",
    publicationDate: "",
    citationCount: "",
    paperPdf: null,
  };

  const [formData, setFormData] = useState(initialState);
  const [fileKey, setFileKey] = useState(Date.now()); // üî• KEY FIX

  const resetForm = () => {
    setFormData(initialState);
    setFileKey(Date.now()); // üî• clears file input UI
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;

    // ‚úÖ FRONTEND PDF CHECK (optional but best UX)
    if (name === "paperPdf" && files[0]) {
      if (files[0].type !== "application/pdf") {
        alert("Only PDF files are allowed ‚ùå");
        resetForm();
        return;
      }
    }

    setFormData({ ...formData, [name]: files ? files[0] : value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    Object.keys(formData).forEach((key) => {
      data.append(key, formData[key]);
    });

    try {
      await axios.post("http://localhost:5000/api/research", data, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("Research Paper + PDF saved ‚úÖ");
      resetForm(); // ‚úÖ clear after success
    } catch (err) {
      alert("Only PDF files allowed ‚ùå");
      console.error(err);
      resetForm(); // ‚úÖ clear after error
    }
  };

  return (
    <div className="research-wrapper">
      <h3 className="section-title">Add Research Paper</h3>

      <form className="research-form" onSubmit={handleSubmit}>
        {[
          ["Paper Title", "paperTitle"],
          ["Authors", "authors"],
          ["Corresponding Author", "correspondingAuthor"],
          ["Journal / Conference Name", "journalName"],
        ].map(([label, name]) => (
          <div className="form-group" key={name}>
            <label>{label}</label>
            <input
              type="text"
              name={name}
              value={formData[name]}
              onChange={handleChange}
              required
            />
          </div>
        ))}

        <div className="form-group">
          <label>Publisher</label>
          <select name="publisher" value={formData.publisher} onChange={handleChange}>
            <option>IEEE</option>
            <option>Springer</option>
            <option>Elsevier</option>
            <option>ACM</option>
            <option>Others</option>
          </select>
        </div>

        <div className="form-group">
          <label>Publication Type</label>
          <select
            name="publicationType"
            value={formData.publicationType}
            onChange={handleChange}
          >
            <option>Journal</option>
            <option>Conference</option>
          </select>
        </div>

        {[
          ["Volume", "volume"],
          ["Issue", "issue"],
          ["Page Numbers", "pageNumbers"],
        ].map(([label, name]) => (
          <div className="form-group" key={name}>
            <label>{label}</label>
            <input name={name} value={formData[name]} onChange={handleChange} />
          </div>
        ))}

        <div className="form-group">
          <label>Publication Date</label>
          <input
            type="date"
            name="publicationDate"
            value={formData.publicationDate}
            onChange={handleChange}
          />
        </div>

        <div className="form-group">
          <label>Citation Count</label>
          <input
            type="number"
            name="citationCount"
            value={formData.citationCount}
            onChange={handleChange}
          />
        </div>

        <div className="form-group full-width">
          <label>Paper PDF Upload</label>
          <input
            key={fileKey}              /* üî• clears file name */
            type="file"
            name="paperPdf"
            accept="application/pdf"
            onChange={handleChange}
            required
          />
        </div>

        <button type="submit" className="submit-btn">
          Add Research Paper
        </button>
      </form>
    </div>
  );
}
