import { useState, useEffect } from "react";
import axios from "axios";
import { useLocation } from "react-router-dom";
import AdminLayout from "./AdminLayout";
import "./AdminExplore.css";

const DEPARTMENTS = [
  "ALL",
  "AI&DS",
  "AI&ML",
  "ASE",
  "CSE",
  "ECE",
  "Civil",
  "Mech",
];

const YEARS = ["I", "II", "III", "IV"];

const DEPT_FULL = {
  "AI&DS": "Artificial Intelligence & Data Science",
  "AI&ML": "Artificial Intelligence & Machine Learning",
  "ASE": "Applied Software Engineering",
  "CSE": "Computer Science & Engineering",
  "ECE": "Electronics & Communication Engineering",
  "Civil": "Civil Engineering",
  "Mech": "Mechanical Engineering",
};

export default function AdminExplore() {
  const location = useLocation();

  /* activity comes from navbar route */
  const activity = location.pathname.replace("/", ""); 
  // research / startups / publications / grants / sih / msme / awards

  const [roles, setRoles] = useState(["student", "faculty"]);
  const [department, setDepartment] = useState("ALL");
  const [years, setYears] = useState([]);
  const [users, setUsers] = useState([]);
  const [searched, setSearched] = useState(false);

  /* RESET when navigating */
  useEffect(() => {
    setUsers([]);
    setSearched(false);
  }, [location.pathname]);

  const toggleRole = (role) => {
    setRoles((prev) =>
      prev.includes(role)
        ? prev.filter((r) => r !== role)
        : [...prev, role]
    );
  };

  const toggleYear = (year) => {
    setYears((prev) =>
      prev.includes(year)
        ? prev.filter((y) => y !== year)
        : [...prev, year]
    );
  };

  const handleSearch = async () => {
    setSearched(true);

    const res = await axios.get("/api/admin/explore/users", {
      params: {
        roles,
        department: department === "ALL" ? "" : department,
        years,
        activity, // ðŸ”¥ key filter
      },
    });

    setUsers(res.data);
  };

  return (
    <AdminLayout>
      <div className="explore-page">

        {/* FILTER PANEL */}
        <aside className="filter-panel">
          <h3>Filter By</h3>

          <div className="filter-group">
            <h4>User Type</h4>
            <label>
              <input
                type="checkbox"
                checked={roles.includes("student")}
                onChange={() => toggleRole("student")}
              />
              Student
            </label>
            <label>
              <input
                type="checkbox"
                checked={roles.includes("faculty")}
                onChange={() => toggleRole("faculty")}
              />
              Faculty
            </label>
          </div>

          <div className="filter-group">
            <h4>Department</h4>
            {DEPARTMENTS.map((d) => (
              <label key={d}>
                <input
                  type="radio"
                  checked={department === d}
                  onChange={() => setDepartment(d)}
                />
                {d === "ALL" ? "All Departments" : d}
              </label>
            ))}
          </div>

          {roles.includes("student") && (
            <div className="filter-group">
              <h4>Year</h4>
              {YEARS.map((y, i) => (
                <label key={y}>
                  <input
                    type="checkbox"
                    checked={years.includes(String(i + 1))}
                    onChange={() => toggleYear(String(i + 1))}
                  />
                  {y}
                </label>
              ))}
            </div>
          )}

          <button className="search-btn" onClick={handleSearch}>
            Search
          </button>
        </aside>

        {/* RESULTS */}
        <main className="results-panel">
          {searched && (
            <h4 className="results-count">
              Results ({users.length})
            </h4>
          )}

          {users.map((u) => (
            <div className="user-card" key={u._id}>
              <div className="user-left">
                <h5 className="user-name">{u.name}</h5>

                <p className="user-sub">
                  {u.role === "student" && (
                    <>
                      {DEPT_FULL[u.department]} Â· Year {u.year}
                    </>
                  )}

                  {u.role === "faculty" && (
                    <>
                      {DEPT_FULL[u.department]}
                    </>
                  )}
                </p>
              </div>

              <button className="view-btn">View Profile</button>
            </div>
          ))}
        </main>

      </div>
    </AdminLayout>
  );
}
