import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Tooltip,
  Legend,
} from "chart.js";
import "./AdminActivityProfile.css";

ChartJS.register(CategoryScale, LinearScale, BarElement, Tooltip, Legend);

/*  Helper to format table headers */
const formatHeader = (key) =>
  key.replace(/([A-Z])/g, " $1").replace(/^./, (c) => c.toUpperCase());

export default function AdminActivityProfile() {
  const { activity, role, id } = useParams();
  const [profile, setProfile] = useState(null);
  const [records, setRecords] = useState([]);

  useEffect(() => {
    axios
      .get(`/api/admin/activity/${activity}/${role}/${id}`)
      .then((res) => {
        setProfile(res.data.profile);
        setRecords(res.data.records);
      })
      .catch(console.error);
  }, [activity, role, id]);

  if (!profile) return <p className="loading">Loading...</p>;

  /*  EXPORT CSV */
  const exportCSV = () => {
    if (!records.length) return;

    const clean = records.map(
      ({ _id, ownerId, ownerRole, __v, paperPdf, ...rest }) => rest
    );

    const rows = [
      Object.keys(clean[0]).join(","),
      ...clean.map((r) => Object.values(r).join(",")),
    ];

    const blob = new Blob([rows.join("\n")], { type: "text/csv" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = `${activity}_${profile.name}.csv`;
    link.click();
  };

  return (
    <div className="activity-profile">

      {/*  COMPACT PROFILE BAR */}
      <div className="profile-bar">
        <h2>{profile.name}</h2>
        <span>
          {role === "student"
            ? `${profile.department} 路 Year ${profile.year}`
            : `${profile.designation} 路 ${profile.department}`}
        </span>
        <button
          onClick={() => {
            localStorage.clear();
            window.location.href = "/admin-login";
          }}
        >
          Logout
        </button>
      </div>

      {/*  METRIC */}
      <div className="metric-box">
        Total {activity.toUpperCase()} : <b>{records.length}</b>
      </div>

      {/*  SMALL CENTERED BAR CHART */}
      <div className="chart-card">
        <h3>{activity.toUpperCase()} Submissions</h3>
        <div className="chart-wrapper">
          <Bar
            data={{
              labels: ["Submitted"],
              datasets: [
                {
                  data: [records.length],
                  backgroundColor: "#2563eb",
                  borderRadius: 8,
                },
              ],
            }}
            options={{
              maintainAspectRatio: false,
              plugins: { legend: { display: false } },
              scales: { y: { ticks: { stepSize: 1, precision: 0 } } },
            }}
          />
        </div>
      </div>

      {/*  TABLE */}
      <div className="table-card">
        <div className="table-header">
          <h3>{activity.toUpperCase()} Details</h3>
          <button className="export-btn" onClick={exportCSV}>
            Export CSV
          </button>
        </div>

        <div className="table-scroll">
          <table>
            <thead>
              <tr>
                {records.length > 0 &&
                  Object.keys(records[0])
                    .filter(
                      (k) =>
                        !["_id", "ownerId", "ownerRole", "__v", "paperPdf"].includes(k)
                    )
                    .map((k) => <th key={k}>{formatHeader(k)}</th>)}
                {activity === "research" && <th>PDF</th>}
              </tr>
            </thead>

            <tbody>
              {records.map((r) => (
                <tr key={r._id}>
                  {Object.keys(r)
                    .filter(
                      (k) =>
                        !["_id", "ownerId", "ownerRole", "__v", "paperPdf"].includes(k)
                    )
                    .map((k) => (
                      <td key={k}>{r[k] ?? "-"}</td>
                    ))}

                  {activity === "research" && (
                    <td>
                      <a
                        className="download-btn"
                        href={`http://localhost:5000/uploads/${r.paperPdf}`}
                        download
                      >
                        Download
                      </a>
                    </td>
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

    </div>
  );
}
