import { useState } from "react";
import axios from "axios";
import AdminLayout from "./AdminLayout";
import "./AdminExplore.css";

const DEPARTMENTS = [
  "AI&DS",
  "AI&ML",
  "ASE",
  "CSE",
  "ECE",
  "Civil",
  "Mech",
];

const YEARS = ["1", "2", "3", "4"];

const DEPT_FULL = {
  "AI&DS": "Artificial Intelligence & Data Science",
  "AI&ML": "Artificial Intelligence & Machine Learning",
  "ASE": "Applied Software Engineering",
  "CSE": "Computer Science & Engineering",
  "ECE": "Electronics & Communication Engineering",
  "Civil": "Civil Engineering",
  "Mech": "Mechanical Engineering",
};

export default function AdminExplore() {
  const [roles, setRoles] = useState(["student"]);
  const [departments, setDepartments] = useState(["ALL"]);
  const [years, setYears] = useState(["ALL"]);
  const [users, setUsers] = useState([]);
  const [searched, setSearched] = useState(false);

  /* TOGGLES */
  const toggleRole = (role) => {
    setRoles((prev) =>
      prev.includes(role) ? prev.filter((r) => r !== role) : [...prev, role]
    );
  };

  const toggleDepartment = (dept) => {
    if (dept === "ALL") {
      setDepartments(["ALL"]);
    } else {
      setDepartments((prev) => {
        const filtered = prev.filter((d) => d !== "ALL");
        return filtered.includes(dept)
          ? filtered.filter((d) => d !== dept)
          : [...filtered, dept];
      });
    }
  };

  const toggleYear = (year) => {
    if (year === "ALL") {
      setYears(["ALL"]);
    } else {
      setYears((prev) => {
        const filtered = prev.filter((y) => y !== "ALL");
        return filtered.includes(year)
          ? filtered.filter((y) => y !== year)
          : [...filtered, year];
      });
    }
  };

  /* SEARCH */
  const handleSearch = async () => {
    setSearched(true);

    const res = await axios.get("/api/admin/explore/users", {
      params: {
        roles,
        departments: departments.includes("ALL") ? [] : departments,
        years: years.includes("ALL") ? [] : years,
      },
    });

    setUsers(res.data);
  };

  return (
    <AdminLayout>
      <div className="explore-page">

        {/* FILTER PANEL */}
        <aside className="filter-panel">
          <h3>Filter By</h3>

          {/* USER TYPE */}
          <div className="filter-group">
            <h4>User Type</h4>
            <label>
              <input
                type="checkbox"
                checked={roles.includes("student")}
                onChange={() => toggleRole("student")}
              />
              Student
            </label>
            <label>
              <input
                type="checkbox"
                checked={roles.includes("faculty")}
                onChange={() => toggleRole("faculty")}
              />
              Faculty
            </label>
          </div>

          {/* DEPARTMENT */}
          <div className="filter-group">
            <h4>Department</h4>

            <label>
              <input
                type="checkbox"
                checked={departments.includes("ALL")}
                onChange={() => toggleDepartment("ALL")}
              />
              All Departments
            </label>

            {DEPARTMENTS.map((d) => (
              <label key={d}>
                <input
                  type="checkbox"
                  checked={departments.includes(d)}
                  onChange={() => toggleDepartment(d)}
                />
                {d}
              </label>
            ))}
          </div>

          {/* YEAR */}
          {roles.includes("student") && (
            <div className="filter-group">
              <h4>Year</h4>

              <label>
                <input
                  type="checkbox"
                  checked={years.includes("ALL")}
                  onChange={() => toggleYear("ALL")}
                />
                All Years
              </label>

              {YEARS.map((y) => (
                <label key={y}>
                  <input
                    type="checkbox"
                    checked={years.includes(y)}
                    onChange={() => toggleYear(y)}
                  />
                  {["I", "II", "III", "IV"][y - 1]}
                </label>
              ))}
            </div>
          )}

          <button className="search-btn" onClick={handleSearch}>
            Search
          </button>
        </aside>

        {/* RESULTS */}
        <main className="results-panel">
          {searched && (
            <h4 className="results-count">
              Results ({users.length})
            </h4>
          )}

          {users.map((u) => (
            <div className="user-card" key={u._id}>
              <div>
                <h5 className="user-name">{u.name}</h5>

                {u.role === "student" && (
                  <p className="user-sub">
                    {DEPT_FULL[u.department]} · Year {u.year}
                  </p>
                )}

                {u.role === "faculty" && (
                  <p className="user-sub faculty">
                    {u.designation} · {DEPT_FULL[u.department]}
                  </p>
                )}
              </div>

              <button className="view-btn">View Profile</button>
            </div>
          ))}
        </main>

      </div>
    </AdminLayout>
  );
}
