// App.js
import React, { useEffect } from "react";
import axios from "axios";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  useLocation,
} from "react-router-dom";

import Navbar from "./components/Navbar";
import RegistrationBoxes from "./components/RegistrationBoxes";
import Footer from "./components/Footer";
import StudentRegistration from "./components/StudentRegistration";
import Login from "./components/Login";
import StudentDashboard from "./components/StudentDashboard";
import AdminLogin from "./components/AdminLogin";
import ResearchPapers from "./components/ResearchPapers";
import ProtectedRoute from "./components/ProtectedRoute";
import ResetPassword from "./components/ResetPassword";
import FacultyRegistration  from "./components/FacultyRegistration"
import FacultyLogin from "./components/FacultyLogin"
import "./App.css";
import FacultyDashboard from "./components/FacultyDashboard";
import FacultyResetPassword from "./components/FacultyResetPassword";
/* ================= AXIOS BASE CONFIG ================= */
axios.defaults.baseURL = "http://localhost:5000";

function AppLayout() {
  const location = useLocation();

  // Hide navbar & footer only on dashboard pages
  const hideNavFooter = ["/student-dashboard"];
  const isHidden = hideNavFooter.includes(location.pathname);

  return (
    <div className="app-wrapper">
      {!isHidden && <Navbar />}

      <main>
        <Routes>
          <Route path="/" element={<RegistrationBoxes />} />
          <Route path="/student-register" element={<StudentRegistration />} />
          <Route path="/login" element={<Login />} />
          <Route path="/admin-login" element={<AdminLogin />} />
          <Route path="/research-papers" element={<ResearchPapers />} />
          <Route path="/faculty-register" element={<FacultyRegistration />} />
          <Route path="/faculty-login" element={<FacultyLogin />} />
          <Route
            path="/student-dashboard"
            element={
              <ProtectedRoute>
                <StudentDashboard />
              </ProtectedRoute>
            }
          />
            <Route path="/faculty-dashboard" element={<FacultyDashboard />} />


                    <Route
            path="/reset-password"
            element={
              <ProtectedRoute>
                <ResetPassword />
              </ProtectedRoute>
            }
          />
          <Route path="/faculty-reset-password" element={<FacultyResetPassword />} />
                  </Routes>
                </main>


                {!isHidden && <Footer />}
              </div>
            );
}

export default function App() {
  useEffect(() => {
    const token = localStorage.getItem("token");

    if (token) {
      axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
    } else {
      delete axios.defaults.headers.common["Authorization"];
    }
  }, []);

  return (
    <Router>
      <AppLayout />
    </Router>
  );
}
