import React, { useState } from "react";
import "./Login.css";

export default function Login() {

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const submitHandler = (e) => {
    e.preventDefault();
    alert("Login Successful!");
  };

  return (
    <div className="login-wrapper">

      {/* LEFT BLUE PANEL */}
      <div className="left-panel">
        <h2 className="left-subtitle">Nice to see you again</h2>
        <h1 className="left-title">WELCOME BACK</h1>

        <p className="left-desc">
          Access your InnovateHub portal, explore insights, and continue your journey
          toward innovation and excellence.
        </p>
      </div>

      {/* RIGHT GLASSMORPHISM PANEL */}
      <div className="right-panel">

        <div className="glass-card">
          <h2 className="login-heading">Login Here</h2>

          <form onSubmit={submitHandler} className="login-form">

            <input
              type="email"
              placeholder="Email ID"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />

            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />

            <button type="submit" className="login-btn">LOGIN</button>
          </form>
        </div>

      </div>

    </div>
  );
}
