import React, { useState } from "react";
import "./Login.css";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const submitHandler = (e) => {
    e.preventDefault();
    alert("Login Successful!");
  };

  return (
    <div className="login-full">

      {/* LEFT SECTION */}
      <div className="left-side">
        <h1 className="left-title">Get Started</h1>
        <p className="left-sub">Already have an account?</p>

        <button className="left-login-btn">Log in</button>
      </div>

      {/* RIGHT SECTION CARD */}
      <div className="right-side">

        <div className="login-card">
          <div className="card-header">
            <h2>Create account</h2>
            <a href="#" className="need-help">Need help?</a>
          </div>

          <form onSubmit={submitHandler}>
            
            <label>Email</label>
            <input 
              type="email" 
              placeholder="Enter email" 
              value={email}
              onChange={(e) => setEmail(e.target.value)} 
              required 
            />

            <label>Password</label>
            <input 
              type="password" 
              placeholder="**********" 
              value={password}
              onChange={(e) => setPassword(e.target.value)} 
              required 
            />

            <label>Full Name</label>
            <input type="text" placeholder="Enter full name" required />

            <div className="terms">
              <input type="checkbox" /> I accept the terms of the agreement
            </div>

            <button type="submit" className="signup-btn">Sign Up</button>
          </form>
        </div>

      </div>

    </div>
  );
}
