import React, { useState } from "react";
import "./Login.css";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const submitHandler = (e) => {
    e.preventDefault();
    alert("Login Successful!");
  };

  return (
    <div className="login-container">

      {/* LEFT SECTION */}
      <div className="login-left">
        <div className="company-name">COMPANY NAME</div>

        <h2 className="welcome-text">Nice to see you again</h2>
        <h1 className="welcome-title">WELCOME BACK</h1>

        <p className="welcome-desc">
          Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed 
          euismod malesuada velit sit amet interdum.
        </p>
      </div>

      {/* RIGHT SECTION */}
      <div className="login-right">
        <h2 className="login-title">Login Account</h2>

        <form onSubmit={submitHandler} className="login-form">

          <input
            type="email"
            placeholder="Email ID"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />

          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          <div className="options-row">
            <label>
              <input type="checkbox" /> Keep me signed in
            </label>

            <a href="/register" className="member-link">
              Already a member?
            </a>
          </div>

          <button type="submit" className="login-btn">SUBSCRIBE</button>
        </form>
      </div>

    </div>
  );
}
