import { useEffect, useState } from "react";
import axios from "axios";
import AdminLayout from "./AdminLayout";
import "./AdminExplore.css";

export default function AdminExplore() {
  const [role, setRole] = useState("student");
  const [department, setDepartment] = useState("");
  const [year, setYear] = useState("");
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetchUsers();
  }, [role, department, year]);

  const fetchUsers = async () => {
    const res = await axios.get("/api/admin/explore/users", {
      params: { role, department, year }
    });
    setUsers(res.data);
  };

  return (
    <AdminLayout>
      <h2>Explore {role === "student" ? "Students" : "Faculty"}</h2>

      <div className="explore-container">
        {/* LEFT FILTERS */}
        <div className="filters">
          <h4>Filter By</h4>

          <label>User Type</label>
          <select value={role} onChange={e => setRole(e.target.value)}>
              <option value="student">Student</option>
              <option value="faculty">Faculty</option>
            </select>

            <select value={department} onChange={e => setDepartment(e.target.value)}>
              <option value="">All Departments</option>
              <option value="CSE">CSE</option>
              <option value="ECE">ECE</option>
              <option value="AI">AI</option>
            </select>

            <select value={year} onChange={e => setYear(e.target.value)}>
              <option value="">All Years</option>
              <option value="1">1</option>
              <option value="2">2</option>
              <option value="3">3</option>
              <option value="4">4</option>
            </select>

        </div>

        {/* RIGHT LIST */}
        <div className="results">
          <h4>Results ({users.length})</h4>

          {users.map(u => (
            <div className="user-card" key={u._id}>
              <div>
                <h5>{u.name}</h5>
                <p>{u.department}</p>
              </div>

              <button
                onClick={() =>
                  alert("Next step: profile modal / page")
                }
              >
                View Profile
              </button>
            </div>
          ))}
        </div>
      </div>
    </AdminLayout>
  );
}
