import React, { useState } from "react";
import "./Chatbot.css";
import { FaComments, FaTimes } from "react-icons/fa";

export default function Chatbot() {
  const [open, setOpen] = useState(false);
  const [messages, setMessages] = useState([
    { from: "bot", text: "Hi ðŸ‘‹ How can I help you today?" }
  ]);
  const [input, setInput] = useState("");

  const sendMessage = () => {
    if (!input.trim()) return;

    const userMsg = { from: "user", text: input };
    const botReply = {
      from: "bot",
      text: "Thanks! Our team will assist you soon ðŸ˜Š"
    };

    setMessages([...messages, userMsg, botReply]);
    setInput("");
  };

  return (
    <>
      {/* ðŸ”˜ Floating Icon */}
      <div className="chatbot-icon" onClick={() => setOpen(true)}>
        <FaComments />
      </div>

      {/* ðŸ’¬ Chat Window */}
      {open && (
        <div className="chatbot-box">
          <div className="chatbot-header">
            <span>InnovateHub Assistant</span>
            <FaTimes className="close-icon" onClick={() => setOpen(false)} />
          </div>

          <div className="chatbot-messages">
            {messages.map((m, i) => (
              <div
                key={i}
                className={`chat-msg ${m.from === "bot" ? "bot" : "user"}`}
              >
                {m.text}
              </div>
            ))}
          </div>

          <div className="chatbot-input">
            <input
              type="text"
              placeholder="Type your message..."
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => e.key === "Enter" && sendMessage()}
            />
            <button onClick={sendMessage}>Send</button>
          </div>
        </div>
      )}
    </>
  );
}
