import React, { useRef, useState, useEffect } from "react";
import Webcam from "react-webcam";
import * as faceapi from "face-api.js";
import axios from "axios";

export default function FaceVerifyModal({ onSuccess, onClose }) {
  const webcamRef = useRef(null);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  /* ===============================
     LOAD MODELS (SAME AS REGISTRATION)
  =============================== */
  useEffect(() => {
    const loadModels = async () => {
      await Promise.all([
        faceapi.nets.tinyFaceDetector.loadFromUri("/models"),
        faceapi.nets.faceLandmark68Net.loadFromUri("/models"),
        faceapi.nets.faceRecognitionNet.loadFromUri("/models"),
      ]);
    };
    loadModels();
  }, []);

  /* ===============================
     VERIFY FACE
  =============================== */
  const verifyFace = async () => {
    setError("");
    setLoading(true);

    const imageSrc = webcamRef.current?.getScreenshot();
    if (!imageSrc) {
      setError("Camera not ready");
      setLoading(false);
      return;
    }

    const img = new Image();
    img.src = imageSrc;

    img.onload = async () => {
      try {
        const options = new faceapi.TinyFaceDetectorOptions({
          inputSize: 224,          // ✅ faster + stable
          scoreThreshold: 0.5,
        });

        const detections = await faceapi
          .detectAllFaces(img, options)
          .withFaceLandmarks()
          .withFaceDescriptors();

        // ❌ No face
        if (detections.length === 0) {
          setError("No face detected. Please look at the camera.");
          setLoading(false);
          return;
        }

        // ❌ Multiple faces
        if (detections.length > 1) {
          setError("Multiple faces detected. Only one person allowed.");
          setLoading(false);
          return;
        }

        // ✅ Exactly ONE face
        const currentDescriptor = Array.from(detections[0].descriptor);

        await axios.post("/api/face/verify", {
          currentDescriptor,
        });

        setLoading(false);
        setError("");
        onSuccess(); // ✅ VERIFIED SUCCESS
      } catch (err) {
        setError("Face does not match registration");
        setLoading(false);
      }
    };
  };

  /* ===============================
     UI
  =============================== */
  return (
    <div className="modal-overlay">
      <div className="modal-box">
        <h3>Face Verification</h3>

        <Webcam
          ref={webcamRef}
          screenshotFormat="image/jpeg"
          className="face-webcam"
          videoConstraints={{ facingMode: "user" }}
        />

        {error && <p className="error">{error}</p>}

        <button onClick={verifyFace} disabled={loading}>
          {loading ? "Verifying..." : "Capture & Verify"}
        </button>

        <button className="cancel" onClick={onClose}>
          Cancel
        </button>
      </div>
    </div>
  );
}
