import React, { useState, useEffect } from "react";
import axios from "axios";
import "./ResearchPapers.css";

export default function ResearchPapers() {
  const initialState = {
    paperTitle: "",
    authors: "",
    correspondingAuthor: "",
    journalName: "",
    publisher: "IEEE",
    publicationType: "Journal",
    volume: "",
    issue: "",
    pageNumbers: "",
    publicationDate: "",
    citationCount: "",
    paperPdf: null,
  };

  const [formData, setFormData] = useState(initialState);
  const [fileKey, setFileKey] = useState(Date.now());
  const [papers, setPapers] = useState([]);

  const resetForm = () => {
    setFormData(initialState);
    setFileKey(Date.now());
  };

  const fetchPapers = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/research");
      setPapers(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleDelete = async (id) => {
  const confirmDelete = window.confirm("Are you sure you want to delete this paper?");

  if (!confirmDelete) return;

  try {
    await axios.delete(`http://localhost:5000/api/research/${id}`);

    // üî• Update UI instantly (remove from state)
    setPapers((prevPapers) =>
      prevPapers.filter((paper) => paper._id !== id)
    );

  } catch (err) {
    alert("Failed to delete paper ‚ùå");
    console.error(err);
  }
};

  useEffect(() => {
    fetchPapers();
  }, []);

  const handleChange = (e) => {
    const { name, value, files } = e.target;

    if (name === "paperPdf" && files[0]) {
      if (files[0].type !== "application/pdf") {
        alert("Only PDF files allowed ‚ùå");
        resetForm();
        return;
      }
    }

    setFormData({ ...formData, [name]: files ? files[0] : value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    Object.keys(formData).forEach((key) => data.append(key, formData[key]));

    try {
      await axios.post("http://localhost:5000/api/research", data);
      alert("Research Paper saved ‚úÖ");
      resetForm();
      fetchPapers();
    } catch (err) {
      alert("Only PDF files allowed ‚ùå");
      resetForm();
    }
  };

  return (
    <>
      {/* ================= YOUR RESEARCH PAPERS ================= */}
      {papers.length > 0 && (
        <div className="papers-section">
          <h3 className="papers-title">Your Research Papers</h3>

          <div className="papers-grid">
            {papers.map((p) => (
              <div className="paper-card" key={p._id}>
  <h4 className="paper-title">{p.paperTitle}</h4>

  <div className="paper-row">
    <span className="paper-label">Authors</span>
    <span className="paper-colon">:</span>
    <span className="paper-value">{p.authors}</span>
  </div>

  <div className="paper-row">
    <span className="paper-label">Publisher</span>
    <span className="paper-colon">:</span>
    <span className="paper-value">{p.publisher}</span>
  </div>

  <div className="paper-row">
    <span className="paper-label">Date</span>
    <span className="paper-colon">:</span>
    <span className="paper-value">
      {p.publicationDate
        ? new Date(p.publicationDate).toLocaleDateString()
        : "-"}
    </span>
  </div>
</div>

            ))}
          </div>
        </div>
      )}

      {/* ================= ADD FORM ================= */}
      <div className="research-wrapper">
        <h3 className="section-title">Add Research Paper</h3>

        <form className="research-form" onSubmit={handleSubmit}>
          {[
            ["Paper Title", "paperTitle"],
            ["Authors", "authors"],
            ["Corresponding Author", "correspondingAuthor"],
            ["Journal / Conference Name", "journalName"],
          ].map(([label, name]) => (
            <div className="form-group" key={name}>
              <label>{label}</label>
              <input name={name} value={formData[name]} onChange={handleChange} required />
            </div>
          ))}

          <div className="form-group">
            <label>Publisher</label>
            <select name="publisher" value={formData.publisher} onChange={handleChange}>
              <option>IEEE</option>
              <option>Springer</option>
              <option>Elsevier</option>
              <option>ACM</option>
              <option>Others</option>
            </select>
          </div>

          <div className="form-group">
            <label>Publication Type</label>
            <select name="publicationType" value={formData.publicationType} onChange={handleChange}>
              <option>Journal</option>
              <option>Conference</option>
            </select>
          </div>

          {["volume", "issue", "pageNumbers"].map((key) => (
            <div className="form-group" key={key}>
              <label>{key.replace(/^\w/, (c) => c.toUpperCase())}</label>
              <input name={key} value={formData[key]} onChange={handleChange} />
            </div>
          ))}

          <div className="form-group">
            <label>Publication Date</label>
            <input type="date" name="publicationDate" value={formData.publicationDate} onChange={handleChange} />
          </div>

          <div className="form-group">
            <label>Citation Count</label>
            <input type="number" name="citationCount" value={formData.citationCount} onChange={handleChange} />
          </div>

          <div className="form-group full-width">
            <label>Paper PDF Upload</label>
            <input key={fileKey} type="file" name="paperPdf" accept="application/pdf" onChange={handleChange} required />
          </div>

          <button type="submit" className="submit-btn">Add Research Paper</button>
        </form>
      </div>
    </>
  );
}
