import React, { useEffect, useState } from "react";
import axios from "axios";
import "./ViewCertificates.css";

export default function ViewCertificates({ role, userId }) {
  const [certs, setCerts] = useState([]);

  useEffect(() => {
    if (!userId) return;

    axios
      .get(`/api/certificates/${role}/${userId}`)
      .then(res => setCerts(res.data))
      .catch(() => setCerts([]));
  }, [role, userId]);

  if (certs.length === 0) {
    return <p className="no-cert">No certificates available</p>;
  }

  return (
    <div className="cert-list">
      {certs.map(c => (
        <div key={c._id} className="cert-card">
          <h4>Your Certificate</h4>
          <p>Issued on: {new Date(c.issuedAt).toDateString()}</p>

         <a
  href={`/uploads/certificates/${c.fileName}`}
  download
  className="download-btn"
>
  Download Certificate (PDF)
</a>

<iframe
  src={`/uploads/certificates/${c.fileName}`}
  className="cert-preview"
  title="Certificate Preview"
/>

        </div>
      ))}
    </div>
  );
}
