import React, { useEffect, useState } from "react";
import axios from "axios";
import "./ViewCertificates.css";

export default function ViewCertificates({ role, userId }) {
  const [certs, setCerts] = useState([]);

  useEffect(() => {
    if (!userId) return;

    console.log("FETCHING CERTS FOR:", role, userId); // ðŸ”¥ DEBUG

    axios
      .get(`/api/certificates/${role}/${userId}`)
      .then(res => {
        console.log("CERT RESPONSE:", res.data); // ðŸ”¥ DEBUG
        setCerts(res.data);
      })
      .catch(err => {
        console.error("CERT FETCH ERROR:", err);
        setCerts([]);
      });
  }, [role, userId]);

  if (certs.length === 0) {
    return <p className="no-cert">No certificates available</p>;
  }

  return (
    <div className="cert-list">
      {certs.map(c => (
        <div key={c._id} className="cert-card">
          <h4>Your Certificate</h4>
          <p>Issued on: {new Date(c.issuedAt).toDateString()}</p>
          <a
            href={`/uploads/certificates/${c.fileName}`}
            target="_blank"
            rel="noopener noreferrer"
          >
            Download Certificate
          </a>
        </div>
      ))}
    </div>
  );
}
