import React, { useState } from "react";
import "./Chatbot.css";
import { FaComments, FaTimes } from "react-icons/fa";

export default function Chatbot() {
  const [open, setOpen] = useState(false);
  const [messages, setMessages] = useState([
    { from: "bot", text: "Hi üëã How can I help you today?" }
  ]);
  const [input, setInput] = useState("");

 const sendMessage = async () => {
  if (!input.trim()) return;

  const userMsg = { from: "user", text: input };
  setMessages((prev) => [...prev, userMsg]);

  try {
    const res = await fetch("http://localhost:5000/api/chatbot/ask", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ message: input }),
    });

    const data = await res.json();

    const botMsg = { from: "bot", text: data.reply };
    setMessages((prev) => [...prev, botMsg]);
  } catch {
    setMessages((prev) => [
      ...prev,
      { from: "bot", text: "‚ö†Ô∏è Server error. Try again." },
    ]);
  }

  setInput("");
};

  return (
    <>
      {/* üîò Floating Icon */}
      <div className="chatbot-icon" onClick={() => setOpen(true)}>
        <FaComments />
      </div>

      {/* üí¨ Chat Window */}
      {open && (
        <div className="chatbot-box">
          <div className="chatbot-header">
            <span>InnovateHub Assistant</span>
            <FaTimes className="close-icon" onClick={() => setOpen(false)} />
          </div>

          <div className="chatbot-messages">
            {messages.map((m, i) => (
              <div
                key={i}
                className={`chat-msg ${m.from === "bot" ? "bot" : "user"}`}
              >
                {m.text}
              </div>
            ))}
          </div>

          <div className="chatbot-input">
            <input
              type="text"
              placeholder="Type your message..."
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => e.key === "Enter" && sendMessage()}
            />
            <button onClick={sendMessage}>Send</button>
          </div>
        </div>
      )}
    </>
  );
}
