import React, { useState } from "react";
import "./StudentRegistration.css";

export default function StudentRegistration() {
  const [formData, setFormData] = useState({
    name: "",
    department: "",
    year: "",
    roll: "",
    email: "",
    phone: "",
    password: "",
    confirmPassword: ""
  });

  const [errors, setErrors] = useState("");

  const departments = ["AI&DS", "AI&ML", "ASE", "CSE", "ECE", "Civil", "Mech"];
  const years = ["I", "II", "III", "IV"];

  // Input change handler
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Password Validation
 const validatePassword = (password) => {
  const regex =
    /^(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{1,8}$/;
  return regex.test(password);
};


  // Email Validation
  const validateEmail = (email) => email.includes("@");

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!validateEmail(formData.email)) {
      setErrors("Email must contain '@'");
      return;
    }

    if (!validatePassword(formData.password)) {
      setErrors(
        "Password must be exactly 6 characters including 1 uppercase, 1 number, and 1 special character"
      );
      return;
    }

    if (formData.password !== formData.confirmPassword) {
      setErrors("Passwords do not match");
      return;
    }

    setErrors("");
    alert("Registration Successful!");
  };

  return (
    <div className="student-container">
      <div className="student-box">
        <h2>Student Registration</h2>

        {errors && <p className="error">{errors}</p>}

        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="name"
            placeholder="Name of the Student"
            value={formData.name}
            onChange={handleChange}
            required
          />

          <select
            name="department"
            value={formData.department}
            onChange={handleChange}
            required
          >
            <option value="">Select Department</option>
            {departments.map((d) => (
              <option key={d} value={d}>{d}</option>
            ))}
          </select>

          <select
            name="year"
            value={formData.year}
            onChange={handleChange}
            required
          >
            <option value="">Year of Study</option>
            {years.map((y) => (
              <option key={y} value={y}>{y}</option>
            ))}
          </select>

          <input
            type="text"
            name="roll"
            placeholder="Roll Number"
            value={formData.roll}
            onChange={handleChange}
            required
          />

          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            required
          />

          <input
            type="text"
            name="phone"
            placeholder="Phone Number"
            value={formData.phone}
            onChange={handleChange}
            required
          />

          <input
            type="password"
            name="password"
            placeholder="Password (6 chars, 1 uppercase, 1 number, 1 special char)"
            value={formData.password}
            onChange={handleChange}
            required
          />

          <input
            type="password"
            name="confirmPassword"
            placeholder="Confirm Password"
            value={formData.confirmPassword}
            onChange={handleChange}
            required
          />

          <button type="submit">Register</button>
        </form>

        <p className="login-text">
          Already Registered? <a href="/login">Login</a>
        </p>
      </div>
    </div>
  );
}
