import React, { useState } from "react";
import "./AdminLogin.css";
import { FaEye, FaEyeSlash } from "react-icons/fa";
import { useNavigate } from "react-router-dom";

export default function AdminLogin() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [showPass, setShowPass] = useState(false);
  const [error, setError] = useState("");

  const navigate = useNavigate();

  const togglePassword = () => setShowPass(!showPass);

  const submitHandler = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const res = await fetch("http://localhost:5000/api/admin/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });

      const data = await res.json();

      if (!res.ok) {
        setError(data.message);
        return;
      }

      // Store admin name for showing in header/dashboard
      localStorage.setItem("adminName", data.username);

      navigate("/admin-dashboard");

    } catch (err) {
      setError("Server error. Try again!");
    }
  };

  return (
    <div className="admin-login-container">

      {/* LEFT SIDE IMAGE BOX */}
      <div className="admin-left">
        <img
          src="/admin.png"
          className="admin-illustration"
          alt="Admin"
        />
      </div>

      {/* RIGHT SIDE LOGIN FORM */}
      <div className="admin-right">

        <h2 className="admin-title">Admin Login</h2>

        <form className="admin-form" onSubmit={submitHandler}>

          <input
            type="text"
            placeholder="Admin Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />

          <div className="password-container">
            <input
              type={showPass ? "text" : "password"}
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <span onClick={togglePassword} className="password-eye">
              {showPass ? <FaEyeSlash /> : <FaEye />}
            </span>
          </div>

          <button className="admin-login-btn">LOGIN</button>

          {error && <p className="admin-error">{error}</p>}
        </form>
      </div>
    </div>
  );
}
